import {
  createWorkflow,
  transform
} from "@medusajs/workflows-sdk"
import { step1, step2 } from "./steps"

type WorkflowInput = {
  name: string
}

type WorkflowOutput = {
  message: string
}

const myWorkflow = createWorkflow<
    WorkflowInput,
    WorkflowOutput
  >
  ("hello-world", (input) => {
    const str1 = step1(input)
    const str2 = step2(input)

    return transform({
      str1,
      str2
    }, (input) => ({
      message: `${input.str1}${input.str2}`
    }))
})
